mapper.update:
UPDATE tbl_g_course
SET course_name=#{course_name},
greenpee=#{greenpee}, golf_time=#{golf_time},
golf_date=#{golf_date},golf_status=#{golf_status},golf_no=#{golf_no}
WHERE course_no=#{course_no}

mapper.select:
select * from tbl_g_course
where name = #{name}
and golf_date = #{golf_date}

SELECT *
from tbl_g_course
WHERE course_no = #{course_no} AND golf_status = 0;
